import React, { useState, useRef, useEffect } from "react";
import * as d3 from "d3";


const SimpleGraph = () => {

        const data = [12, 36, 6, 25, 35, 10];
        const w = 500;
        const h = 400;

        const accessToRef= d3.select(this.myRef.current);
            append("svg")
            .attr("width", w)
            .attr("height", h)
            .style("background-color", "#cccccc")
            .style("padding", 10)
            .style("margin-left", 50)

        accessToRef.selectAll("rect")
            .data(data)
            .enter()
            .append("rect")
            .attr("x", (d, i) => i * 70)
            .attr("y", (d, i) => h - 10 * d)
            .attr("width", 65)
            .attr("height", (d, i) => d * 10)
            .attr("fill", "tomato");

        return (
        <div>
            <svg ref={this.myRef}></svg>
        </div>
        )
}  

export default SimpleGraph;
