import * as d3 from "d3";


class SimpleGraph extends Component {

    constructor(props) {
        super(props);
        this.myref = React.createRef();
    }

    componentDidMount() {
        let accessToRef= d3.select(this.myRef.current);
        accessToRef.style("background-color", "red ")
    }

    render() {
        return (
        <div ref={this.myRef}>
           Testing Refs
        </div>
        )
    }
}
